statements.statement:

         /* 해외가맹점명 조회 */
SELECT /*+ 김민성::::/card/MyDtVovsAthHisEbc/retvLstMydtAthHis/retvMerchantName */ 
      A.해외가맹점명
    , A.승인도시명
 FROM INSTC.TBUBDI003 A /* TBGBHA802 A */
WHERE A.회원일련번호 = ${회원일련번호}
  AND A.매출승인번호 = ${매출승인번호}
  AND A.승인년월일  = ${승인년월일}
  AND A.매출승인시각 = ${매출승인시각}
  AND ROWNUM = 1
