   ,${SYS_DT}
   ,${TRAN_HEADER}
   ,${IN_DATA1}
   ,${IN_DATA2}
)


SELECT ROWNUM
      ,A.*
FROM (
    SELECT  ROWIDTOCHAR( ROWID ) DATA_ROW_ID
           --,PROC_TM
    FROM    DEVON.SERE_DFRDDATA_TB01
    WHERE   DFRD_SEQ = 0
    AND     ( PROC_TM <= ${PROC_TM} OR PROC_TM = '999999999' )            
    AND     TRAN_DT = ${TRAN_DT}
    AND     DFRD_BIZ_ID = ${DFRD_BIZ_ID}
    AND     INST_NO = ${INST_NO}
    AND     NODE_NO = ${NODE_NO}
    --ORDER BY PROC_TM
    ) A
WHERE ROWNUM <= ${ROW_CNT}


