statements.statement:

SELECT DFRD_BIZ_ID
  FROM DEVON.SERE_DFRDBIZ_TB
 WHERE SITE_CD = ${SITE_CD}


SELECT DFRD_PROC_ID
      ,BLOCK_MAX_CNT
FROM  DEVON.SERE_DFRDPROC_TB
WHERE DFRD_BIZ_ID = ${DFRD_BIZ_ID}
AND   SITE_CD = ${SITE_CD}


SELECT NVL(MAX(DFRD_BLOCK_SEQ), 0 ) DFRD_BLOCK_SEQ
FROM DEVON.SERE_DFRDDATBLCK_TB
WHERE TRAN_DT = ${TRAN_DT}
AND   DAY_CD = SUBSTR( ${TRAN_DT}, 7, 2 )
AND   DFRD_PROC_ID = ${DFRD_PROC_ID}


SELECT  LAST_PROC_NM
       ,DFRD_GUID
       ,DFRD_PATH_NO
FROM    DEVON.SERE_DFRDDATPROC_TB
WHERE   TRAN_DT      = ${TRAN_DT}
AND     DAY_CD       = SUBSTR(${TRAN_DT},7,2)
AND     DFRD_PROC_ID = ${DFRD_PROC_ID}
AND     NODE_NO      = ${NODE_NO}
AND     INST_NO      = ${INST_NO}
AND     DFRD_SEQ     = ${DFRD_SEQ}


SELECT COUNT(1) DFRD_BLOCK_CNT
FROM DEVON.SERE_DFRDDATBLCK_TB
WHERE TRAN_DT = ${TRAN_DT}
AND   DAY_CD = SUBSTR( ${TRAN_DT}, 7, 2 )
AND   DFRD_PROC_ID = ${DFRD_PROC_ID}
AND   PROC_ST = ${PROC_ST}


SELECT DFRD_PROC_ID
      ,MAX_THREAD_CNT
      ,CFM_CHK_INT
FROM DEVON.SERE_DFRDPROCJOB_TB
WHERE DFRD_ID = ${DFRD_ID}
AND   SITE_CD = ${SITE_CD}


SELECT DFRD_ID
FROM DEVON.SERE_DFRDPROCJOB_TB
WHERE DFRD_PROC_ID = ${DFRD_PROC_ID}
AND   SITE_CD = ${SITE_CD}


INSERT INTO DEVON.SERE_DFRDDATPROC_TB
(
    TRAN_DT
   ,DAY_CD
   ,INST_NO
   ,NODE_NO
   ,DFRD_PROC_ID
   ,DFRD_SEQ
   ,PROC_ST
   ,LAST_PROC_NM
   ,PROC_STRT_DT
   ,PROC_END_DT
)
VALUES
(
    ${TRAN_DT}
   ,${DAY_CD}
   ,${INST_NO}
   ,${NODE_NO}
   ,${DFRD_PROC_ID}
   ,${DFRD_SEQ}
   ,${PROC_ST}
   , 0
   ,' '
   ,' '
)


UPDATE  DEVON.SERE_DFRDDATPROC_TB
   SET  PROC_ST      = ${PROC_ST}
       ,PROC_STRT_DT = ${PROC_STRT_DT}
       ,PROC_END_DT  = ${PROC_END_DT}
       ,LAST_PROC_NM = ${LAST_PROC_NM}
       ,DFRD_GUID    = ${DFRD_GUID}
       ,DFRD_PATH_NO = ${DFRD_PATH_NO}
       ,OGTN_GUID    = ${OGTN_GUID}
       ,OGTN_PATH_NO = ${OGTN_PATH_NO}
 WHERE  TRAN_DT      = ${TRAN_DT}
   AND  DAY_CD       = SUBSTR(${TRAN_DT},7,2)
   AND  DFRD_PROC_ID = ${DFRD_PROC_ID}
   AND  DFRD_SEQ     = ${DFRD_SEQ}
   AND  NODE_NO      = ${NODE_NO}
   AND  INST_NO      = ${INST_NO}


UPDATE DEVON.SERE_DFRDDATBLCK_TB
   SET PROC_ST = ${PROC_ST}
      ,DIST_END_DT = ${DIST_END_DT}
      ,PROC_STRT_DT = ${PROC_STRT_DT}
      ,PROC_END_DT = ${PROC_END_DT}
      ,PROC_INST_NM = ${PROC_INST_NM}
      ,PROC_DFRD_ID = ${PROC_DFRD_ID}
      ,PROC_THREAD_ID = ${PROC_THREAD_ID}
      ,NOR_END_CNT = ${NOR_END_CNT}
      ,ERR_END_CNT = ${ERR_END_CNT}
      ,CFM_END_CNT = ${CFM_END_CNT}
 WHERE
       TRAN_DT = ${TRAN_DT}
   AND DAY_CD = SUBSTR( ${TRAN_DT}, 7, 2 )
   AND DFRD_PROC_ID = ${DFRD_PROC_ID}
   AND DFRD_BLOCK_SEQ = ${DFRD_BLOCK_SEQ}
   AND NODE_NO = ${NODE_NO}


UPDATE DEVON.SERE_DFRDBIZMNG_TB
   SET MOD_DT = TO_CHAR(SYSTIMESTAMP, 'YYYYMMDDHH24MISSFF3')
      ,MOD_USER_ID = ${MOD_USER_ID}
      ,LAST_DFRD_SEQ = ${LAST_DFRD_SEQ}
      ,TRAN_DT = ${TRAN_DT}
WHERE  DFRD_BIZ_ID = ${DFRD_BIZ_ID}
   AND SITE_CD = ${SITE_CD}


INSERT INTO DEVON.SERE_DFRDPROCERR_TB
