(
    ${TRAN_DT}
   ,${DFRD_PROC_ID}
   ,${DFRD_SEQ}
   ,${PROC_SEQ}
   ,${DFRD_GUID}
   ,${DFRD_PATH_NO}
   ,TO_CHAR(SYSTIMESTAMP, 'YYYYMMDDHH24MISSFF3')
   ,${ERR_CD}
   ,${ACTION_CD}
   ,${ACTION_MSG}
   ,${USER_MSG}
   ,${ERR_TRACE_TYPE}
   ,${ERR_TRACE}
   ,${ERR_TRACE_LARGE}
