SELECT NVL(MAX(PROC_SEQ), 0 ) PROC_SEQ
FROM DEVON.SERE_DFRDPROCERR_TB
WHERE TRAN_DT = ${TRAN_DT}
AND   DFRD_PROC_ID = ${DFRD_PROC_ID}
AND   DFRD_SEQ = ${DFRD_SEQ}


SELECT NVL(MIN(DFRD_BLOCK_SEQ), 0 ) DFRD_BLOCK_SEQ
FROM DEVON.SERE_DFRDDATBLCK_TB
WHERE TRAN_DT = ${TRAN_DT}
AND   DAY_CD = SUBSTR( ${TRAN_DT}, 7, 2 )
AND   DFRD_PROC_ID = ${DFRD_PROC_ID}
AND   PROC_ST IN ( '00' , '09', '20' )


SELECT  MAX(DFRD_BLOCK_SEQ) DFRD_BLOCK_SEQ
FROM
(
    SELECT  NVL(MIN(DFRD_BLOCK_SEQ), 0 ) DFRD_BLOCK_SEQ
    FROM    DEVON.SERE_DFRDDATBLCK_TB
    WHERE   ${PROC_DFRD_ID} IS NULL
    AND     TRAN_DT = ${TRAN_DT}
    AND     DAY_CD = SUBSTR( ${TRAN_DT} , 7, 2 )
    AND     DFRD_PROC_ID = ${DFRD_PROC_ID}
    AND     PROC_ST = ${PROC_ST}
    UNION ALL
    SELECT  NVL(MIN(DFRD_BLOCK_SEQ), 0 ) DFRD_BLOCK_SEQ
    FROM    DEVON.SERE_DFRDDATBLCK_TB
    WHERE   ${PROC_DFRD_ID} IS NOT NULL
    AND     TRAN_DT = ${TRAN_DT}
    AND     DAY_CD = SUBSTR( ${TRAN_DT} , 7, 2 )
    AND     DFRD_PROC_ID = ${DFRD_PROC_ID}
    AND     PROC_ST = ${PROC_ST}
    AND     PROC_DFRD_ID = ${PROC_DFRD_ID}
)


SELECT  *
FROM    (
            SELECT  *
            FROM    DEVON.SERE_DFRDDATBLCK_TB
            WHERE TRAN_DT = ${TRAN_DT}
            AND DAY_CD  = SUBSTR(${TRAN_DT}, 7, 2)
            AND NODE_NO = ${NODE_NO}
            AND DFRD_PROC_ID = ${DFRD_PROC_ID}
            AND DFRD_BLOCK_SEQ >= ${DFRD_BLOCK_SEQ}
            ORDER BY DFRD_BLOCK_SEQ
        )
WHERE   ROWNUM < 100


SELECT  *
FROM    (
            SELECT  *
            FROM    DEVON.SERE_DFRDDATBLCK_TB
            WHERE   TRAN_DT = ${TRAN_DT}
            AND     DAY_CD  = SUBSTR(${TRAN_DT}, 7, 2)
            AND     NODE_NO = ${NODE_NO}
            AND     DFRD_PROC_ID = ${DFRD_PROC_ID}
            AND     DFRD_BLOCK_SEQ > 0
            AND     PROC_ST IN ('00', '01', '10', '99', '91')
            --ORDER BY DFRD_BLOCK_SEQ
        )
WHERE   ROWNUM < 100


  SELECT TRAN_DT
        ,DFRD_PROC_ID
        ,DFRD_SEQ
        ,PROC_ST
        ,PROC_STRT_DT
        ,PROC_END_DT
    FROM DEVON.SERE_DFRDDATPROC_TB
   WHERE TRAN_DT = ${TRAN_DT}
     AND DAY_CD  = SUBSTR(TRAN_DT,7,2)
     AND NODE_NO = ${NODE_NO}
     AND INST_NO = ${INST_NO}
     AND DFRD_PROC_ID = ${DFRD_PROC_ID}
     AND DFRD_SEQ BETWEEN ${STRT_DFRD_SEQ:long} AND ${END_DFRD_SEQ:long}
--ORDER BY DFRD_SEQ


UPDATE DEVON.SERE_DFRDMAIN_TB
   SET AUTOEXE_YN = ${AUTOEXE_YN}
     , MOD_DT = ${MOD_DT}
     , MOD_USER_ID = ${MOD_USER_ID} 
 WHERE 1=1
   AND SITE_CD = ${SITE_CD}
   AND USE_YN = 'Y'
   AND DFRD_ID LIKE 'M______0'  /* 분배잡은 맨마지막이 0 */
