statements.statement:

INSERT /*+ 김남식::/gcb/batch/cmnDatSynchMg/cmnDatRcp/empClwrAltmInfLdinJob/EmpClwrAltmInfLdin/regtClwrAltmInf */
  INTO INSTC.TBGCBHM01
(
   부점코드
 , 사무분담코드
 , 직원번호
 , 적용시작년월일
 , 적용종료년월일
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
 , 대직여부
)
VALUES
(
  ${부점코드}
 ,${사무분담코드}
 ,${직원번호}
 ,${적용시작년월일}
 ,${적용종료년월일}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
 ,${대직여부}
)


DELETE /*+ 김남식::/gcb/batch/cmnDatSynchMg/cmnDatRcp/empClwrAltmInfLdinJob/EmpClwrAltmInfLdin/delClwrAltmInf */
  FROM INSTC.TBGCBHM01  
 WHERE 부점코드    = ${부점코드}
   AND 사무분담코드  = ${사무분담코드}
   AND 직원번호    = ${직원번호}
   AND 적용시작년월일 = ${적용시작년월일}


UPDATE /*+ 김남식::/gcb/batch/cmnDatSynchMg/cmnDatRcp/empClwrAltmInfLdinJob/EmpClwrAltmInfLdin/uptClwrAltmInf */ 
       INSTC.TBGCBHM01 
   SET 적용종료년월일    = ${적용종료년월일}
     , 시스템최종갱신일시  = SYSTIMESTAMP
     , 시스템최종갱신식별자 = ${시스템최종갱신식별자}
     , 시스템최종거래일시  = ${시스템최종거래일시}
     , 대직여부  = ${대직여부}
 WHERE 부점코드    = ${부점코드}
   AND 사무분담코드  = ${사무분담코드}
   AND 직원번호    = ${직원번호}
   AND 적용시작년월일 = ${적용시작년월일}
