statements.statement:

SELECT /*+ 위상규::/gcb/bchVlnMg/gcbBchVlnBasEbi/TBGCBMB01/retvGcbBchVlnBasMxClSno */ 
       A.배치작업검증분류일련번호
  FROM INSTC.TBGCBMB01 A 
 WHERE A.어플리케이션구분코드   = ${어플리케이션구분코드}
   AND A.배치작업검증대분류내용  = ${배치작업검증대분류내용}
   AND A.배치작업검증중분류내용  = ${배치작업검증중분류내용}
   AND A.배치작업검증소분류내용  = ${배치작업검증소분류내용}
   AND ROWNUM = 1
 ORDER BY A.배치작업검증분류일련번호 DESC


INSERT /*+ 위상규::/gcb/bchVlnMg/gcbBchVlnBasEbi/TBGCBMB01/regtGcbBchVlnBas */
  INTO INSTC.TBGCBMB01
(
   어플리케이션구분코드
 , 배치작업검증대분류내용
 , 배치작업검증중분류내용
 , 배치작업검증소분류내용
 , 배치작업검증분류일련번호
 , 배치작업검증일련번호
 , 배치작업검증담당자SMS수신여부
 , 배치작업검증구분코드
 , 배치작업검증오차허용범위율
 , 검증대상배치작업명
 , 작업주기구분코드
 , 작업주기세부구분코드
 , 배치작업시작시각
 , 배치작업종료시각
 , IT운영SMS일련번호
 , IT운영SMS부가일련번호
 , 등록년월일
 , 등록부점코드
 , 등록직원번호
 , 해제년월일
 , 해제부점코드
 , 해제직원번호
 , 배치작업검증업무내용
 , 등록상태구분코드
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${어플리케이션구분코드}
 ,${배치작업검증대분류내용}
 ,${배치작업검증중분류내용}
 ,${배치작업검증소분류내용}
 ,${배치작업검증분류일련번호}
 ,${배치작업검증일련번호}
 ,NVL(${배치작업검증담당자SMS수신여부}, '0')
 ,${배치작업검증구분코드}
 ,${배치작업검증오차허용범위율}
 ,${검증대상배치작업명}
 ,${작업주기구분코드}
 ,${작업주기세부구분코드}
 ,${배치작업시작시각}
 ,${배치작업종료시각}
 ,${IT운영SMS일련번호}
 ,${IT운영SMS부가일련번호}
 ,${등록년월일}
 ,${등록부점코드}
 ,${등록직원번호}
 ,${해제년월일}
 ,${해제부점코드}
 ,${해제직원번호}
 ,${배치작업검증업무내용}
 ,${등록상태구분코드}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)
