statements.statement:

INSERT /*+ 이태양::/gcb/etcInfMg/gcbTrmlSceRsbpBasEbi/TBGCBHT01/regtGcbTrmlSceRsbpBas */
  INTO INSTC.TBGCBHT01        /* GCB단말화면담당자기본 */
(
   화면번호
 , 화면호출번호
 , 화면명
 , 화면전산담당직원번호
 , 화면전산담당자전화번호
 , 화면현업담당직원번호
 , 화면현업담당자전화번호
 , 등록직원번호
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${화면번호}
 ,${화면호출번호}
 ,${화면명}
 ,${화면전산담당직원번호}
 ,${화면전산담당자전화번호}
 ,${화면현업담당직원번호}
 ,${화면현업담당자전화번호}
 ,${등록직원번호}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


UPDATE /*+ 이태양::/gcb/etcInfMg/gcbTrmlSceRsbpBasEbi/TBGCBHT01/uptGcbTrmlSceRsbpBas */ 
       INSTC.TBGCBHT01        /* GCB단말화면담당자기본 */
   SET 화면호출번호      = ${화면호출번호}
     , 화면명         = ${화면명}
     , 화면전산담당직원번호  = ${화면전산담당직원번호}
     , 화면전산담당자전화번호 = ${화면전산담당자전화번호}
     , 화면현업담당직원번호  = ${화면현업담당직원번호}
     , 화면현업담당자전화번호 = ${화면현업담당자전화번호}
     , 등록직원번호      = ${등록직원번호}
     , 시스템최종갱신일시   = SYSTIMESTAMP
     , 시스템최종갱신식별자  = ${시스템최종갱신식별자}
     , 시스템최종거래일시   = ${시스템최종거래일시}
 WHERE 화면번호 = ${화면번호}


DELETE /*+ 이태양::/gcb/etcInfMg/gcbTrmlSceRsbpBasEbi/TBGCBHT01/delGcbTrmlSceRsbpBas */
  FROM INSTC.TBGCBHT01        /* GCB단말화면담당자기본 */
 WHERE 화면번호 = ${화면번호}
