SELECT /*+ 김동현::/gcb/ingCdMg/gcbIngCdEbi/TBGCBUI01/retvLstIngCdPupCtgInqByIngCdNm */ 
       A.통합코드명
     , A.통합코드영문명
     , B.통합코드
     , B.통합코드내용
  FROM INSTC.TBGCBUI01 A
     , INSTC.TBGCBUI02 B
 WHERE A.통합코드명 = B.통합코드명
   AND A.적용시작년월일 <= ${적용시작년월일}
   AND A.적용종료년월일 >= ${적용종료년월일}
   AND B.적용시작년월일 <= ${적용시작년월일}
   AND B.적용종료년월일 >= ${적용종료년월일}
   AND A.통합코드명  = ${통합코드명}
