SELECT /*+ 위상규::/gcb/itObstMgSpt/errTrMng/errTrMngDmfreec/ErrTrMng/retvPdPrcErrTrEcpPrcTg */ 
       COUNT(1) 발생건수_N9
     , MAX(A.상품처리오류거래건수) 최대건수_N9
     , AVG(A.상품처리오류거래건수) 평균건수_N9_2
  FROM INSTC.TBGCBEM12 A        /* GCB오류거래모니터링오류집계 */
 WHERE A.상품처리거래집계구분코드 = ${상품처리거래집계구분코드}
   AND A.상품처리거래집계년월일  >= TO_CHAR(ADD_MONTHS(TO_DATE(${상품처리거래집계년월일}, 'YYYYMMDD'), -1), 'YYYYMMDD')
   AND A.상품처리거래집계년월일  < TO_CHAR(SYSDATE, 'YYYYMMDD')
   AND A.상품처리거래집계시분 = ${상품처리거래집계시분}
   AND A.채널구분코드       = ${채널구분코드}
   AND A.채널세부업무구분코드   = ${채널세부업무구분코드}
   AND A.거래코드         = ${거래코드}
   AND '01' = ${상품처리거래검증대상구분코드}
   AND A.상품처리오류거래건수 >= ${상품처리오류거래건수}
 GROUP BY A.상품처리거래집계구분코드
     , A.상품처리거래집계시분
     , A.채널구분코드
     , A.채널세부업무구분코드
     , A.거래코드
 UNION ALL
SELECT COUNT(1) 발생건수_N9
     , MAX(A.상품처리오류거래건수) 최대건수_N9
     , AVG(A.상품처리오류거래건수) 평균건수_N9_2
  FROM INSTC.TBGCBEM12 A        /* GCB오류거래모니터링오류집계 */
 WHERE A.상품처리거래집계구분코드 = ${상품처리거래집계구분코드}
   AND A.상품처리거래집계년월일  >= TO_CHAR(ADD_MONTHS(TO_DATE(${상품처리거래집계년월일}, 'YYYYMMDD'), -1), 'YYYYMMDD')
   AND A.상품처리거래집계년월일  < TO_CHAR(SYSDATE, 'YYYYMMDD')
   AND A.상품처리거래집계시분 = ${상품처리거래집계시분}
   AND A.채널구분코드       = ${채널구분코드}
   AND A.채널세부업무구분코드   = ${채널세부업무구분코드}
   AND A.거래코드 = ${거래코드}
   AND A.오류메시지코드 = ${오류메시지코드}
   AND '02' = ${상품처리거래검증대상구분코드}
   AND A.상품처리오류거래건수 >= ${상품처리오류거래건수}
 GROUP BY A.상품처리거래집계구분코드
     , A.상품처리거래집계시분
     , A.채널구분코드
     , A.채널세부업무구분코드
     , A.거래코드
     , A.오류메시지코드
 UNION ALL
SELECT COUNT(1) 발생건수_N9
     , MAX(A.상품처리오류거래건수) 최대건수_N9
