INSERT /*+ 김은령::/gcb/itObstMgSpt/gcbBwkChpSmsTgLdgEbi/TBGCBEM22/regtGcbBwkChpSmsTgLdg */
  INTO INSTC.TBGCBEM22        /* GCB업무점검SMS대상원장 */
(
   어플리케이션코드
 , 업무점검번호
 , SMS전송대상자식별내용
 , SMS전송요일구분코드
 , 미점검SMS대상여부
 , 일괄SMS대상여부
 , 일괄SMS전송시분
 , 즉시SMS대상여부
 , 즉시SMS대상구분코드
 , 부가숫자시작값
 , 부가숫자종료값
 , 부가문자값
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${어플리케이션코드}
 ,${업무점검번호}
 ,${SMS전송대상자식별내용}
 ,${SMS전송요일구분코드}
 ,${미점검SMS대상여부}
 ,${일괄SMS대상여부}
 ,${일괄SMS전송시분}
 ,${즉시SMS대상여부}
 ,${즉시SMS대상구분코드}
 ,${부가숫자시작값}
 ,${부가숫자종료값}
 ,${부가문자값}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


UPDATE /*+ 김은령::/gcb/itObstMgSpt/gcbBwkChpSmsTgLdgEbi/TBGCBEM22/uptGcbBwkChpSmsTgLdg */ 
       INSTC.TBGCBEM22        /* GCB업무점검SMS대상원장 */
   SET SMS전송요일구분코드 = ${SMS전송요일구분코드}
     , 미점검SMS대상여부  = ${미점검SMS대상여부}
     , 일괄SMS대상여부   = ${일괄SMS대상여부}
     , 일괄SMS전송시분   = ${일괄SMS전송시분}
     , 즉시SMS대상여부   = ${즉시SMS대상여부}
     , 즉시SMS대상구분코드 = ${즉시SMS대상구분코드}
     , 부가숫자시작값     = ${부가숫자시작값}
     , 부가숫자종료값     = ${부가숫자종료값}
     , 부가문자값       = ${부가문자값}
     , 시스템최종갱신일시   = SYSTIMESTAMP
     , 시스템최종갱신식별자  = ${시스템최종갱신식별자}
     , 시스템최종거래일시   = ${시스템최종거래일시}
 WHERE 어플리케이션코드     = ${어플리케이션코드}
   AND 업무점검번호       = ${업무점검번호}
   AND SMS전송대상자식별내용 = ${SMS전송대상자식별내용}


DELETE /*+ 김은령::/gcb/itObstMgSpt/gcbBwkChpSmsTgLdgEbi/TBGCBEM22/delGcbBwkChpSmsTgLdg */
  FROM INSTC.TBGCBEM22        /* GCB업무점검SMS대상원장 */
 WHERE 어플리케이션코드     = ${어플리케이션코드}
   AND 업무점검번호       = ${업무점검번호}
   AND SMS전송대상자식별내용 = ${SMS전송대상자식별내용}


SELECT /*+ 김은령::/gcb/itObstMgSpt/gcbBwkChpSmsTgLdgEbi/TBGCBEM22/retvGcbBwkChpSmsTgNcn */ 
       COUNT(1) AS 조회건수_N6
  FROM INSTC.TBGCBEM22 A        /* GCB업무점검SMS대상원장 */
 WHERE A.어플리케이션코드 = ${어플리케이션코드}
   AND A.업무점검번호   = ${업무점검번호}
