statements.statement:

INSERT /*+ 위상규::/gcb/itObstMgSpt/gcbErrTrMngNmlSmmEbi/TBGCBEM11/regtGcbErrTrMngNmlSmm */
  INTO INSTC.TBGCBEM11        /* GCB오류거래모니터링정상집계 */
(
   상품처리거래집계구분코드
 , 상품처리거래집계년월일
 , 상품처리거래집계시분
 , 채널구분코드
 , 채널세부업무구분코드
 , 거래코드
 , 상품처리총거래건수
 , 상품처리정상거래건수
 , 상품처리정상거래소요합계초수
 , 상품처리정상거래소요최소초수
 , 상품처리정상거래소요최대초수
 , 상품처리오류거래건수
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${상품처리거래집계구분코드}
 ,${상품처리거래집계년월일}
 ,${상품처리거래집계시분}
 ,${채널구분코드}
 ,${채널세부업무구분코드}
 ,${거래코드}
 ,${상품처리총거래건수}
 ,${상품처리정상거래건수}
 ,${상품처리정상거래소요합계초수}
 ,${상품처리정상거래소요최소초수}
 ,${상품처리정상거래소요최대초수}
 ,${상품처리오류거래건수}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


DELETE /*+ 위상규::/gcb/itObstMgSpt/gcbErrTrMngNmlSmmEbi/TBGCBEM11/delGcbErrTrMngNmlSmm */
  FROM INSTC.TBGCBEM11        /* GCB오류거래모니터링정상집계 */
 WHERE 상품처리거래집계구분코드 = ${상품처리거래집계구분코드}
   AND 상품처리거래집계년월일  = ${상품처리거래집계년월일}
   AND 상품처리거래집계시분   = ${상품처리거래집계시분}
   AND 채널구분코드       = ${채널구분코드}
   AND 채널세부업무구분코드   = ${채널세부업무구분코드}
   AND 거래코드         = ${거래코드}
