INSERT /*+ 김동현::/gcb/ldgAmdMg/gcbLdgAmdDatExeHisEbi/TBGCBLA17/regtGcbLdgAmdDatExeHisRgByBch */
  INTO INSTC.TBGCBLA17        /* GCB원장수정데이터실행내역 */
(
   원장수정의뢰번호
 , 원장수정IT담당팀구분코드
 , 원장수정대상서버구분코드
 , 어플리케이션코드
 , 원장수정담당직원번호
 , 원장수정처리SQL일련번호
 , 원장수정처리SQL구분코드
 , 관리일련번호
 , 원장수정처리SQL내용
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${원장수정의뢰번호}
 ,${원장수정IT담당팀구분코드}
 ,${원장수정대상서버구분코드}
 ,${어플리케이션코드}
 ,${원장수정담당직원번호}
 ,(SELECT NVL(MAX(원장수정처리SQL일련번호), 0)+1
     FROM INSTC.TBGCBLA17
    WHERE 원장수정의뢰번호 = ${원장수정의뢰번호}
      AND 원장수정IT담당팀구분코드 = ${원장수정IT담당팀구분코드}
      AND 원장수정대상서버구분코드 = ${원장수정대상서버구분코드}
      AND 어플리케이션코드 = ${어플리케이션코드}
      AND 원장수정담당직원번호 = ${원장수정담당직원번호}
      AND 원장수정처리SQL구분코드 = ${원장수정처리SQL구분코드}
      AND 관리일련번호 = ${관리일련번호})
 ,${원장수정처리SQL구분코드}
 ,${관리일련번호}
 ,${원장수정처리SQL내용}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


