planed-shutdown.insert-query:

INSERT INTO DEVON.SERE_PLANED_SHUTDOWN_TB
(
    TRAN_DT             -- 거래일자
   ,SEQ                 -- 처리순서
   ,GUID                -- GUID
   ,SYS_PATH_NO         -- 진행번호
   ,TR_CD               -- 거래코드
   ,INTF_ID             -- 인터페이스ID
   ,CHN_DTCD            -- 채널구분코드
   ,CHN_DTLS_BWK_DTCD   -- 채널세부업무구분코드
   ,SCE_NO              -- 화면번호
   ,TR_EMPNO            -- 거래직원번호
   ,TR_BRCD             -- 거래부점코드
   ,TMNO                -- 단말번호
   ,HOST_NM             -- 호스트명
   ,INST_NM             -- 인스턴스명
   ,QUERY_EXE_TY        -- 쿼리타입 (0:XML, 1:REAL QUERY)
   ,QUERY_PATH          -- 쿼리PATH
   ,QUERY1              -- REAL QUERY
   ,QUERY2              -- REAL QUERY
   ,PARAM               -- 파라미터 (JSON)
   ,QUERY_EXE_TIME      -- 쿼리수행TIMESTAMP
   ,PROC_ST             -- 처리상태 (0:미처리, 1:처리완료, 9:처리오류)
   ,TABLE_NM
   ,TABLE_PRIMARY_KEY
) VALUES 
(
     ${TRAN_DT}
    ,DEVON.SERE_PLANED_SHUTDOWN_TB_SEQ.NEXTVAL
    ,${GUID}
    ,${SYS_PATH_NO}
    ,${TR_CD}
    ,${INTF_ID}
    ,${CHN_DTCD}
    ,${CHN_DTLS_BWK_DTCD}
    ,${SCE_NO}
    ,${TR_EMPNO}
    ,${TR_BRCD}
    ,${TMNO}
    ,${HOST_NM}
    ,${INST_NM}
    ,${QUERY_EXE_TY}
    ,${QUERY_PATH}
    ,${QUERY1}
    ,${QUERY2}
    ,${PARAM}
    ,CURRENT_TIMESTAMP
    ,${PROC_ST}
    ,${TABLE_NM}
    ,${TABLE_PRIMARY_KEY}
)

planed-shutdown.update-query:

UPDATE DEVON.SERE_PLANED_SHUTDOWN_TB
SET    PROC_ST  = ${PROC_ST}
WHERE   1 = 1
AND     TRAN_DT = ${TRAN_DT}
AND     GUID    = ${GUID}
