SELECT ROWNUM
      ,A.*
FROM (
    SELECT  ROWIDTOCHAR( ROWID ) DATA_ROW_ID
           --,PROC_TM
    FROM    DEVON.SERL_APPLOG_LG01
    WHERE   DFRD_SEQ = 0
    AND     TRAN_DT = ${TRAN_DT}
    AND     DFRD_BIZ_ID = ${DFRD_BIZ_ID}
    AND     INST_NO = ${INST_NO}
    AND     NODE_NO = ${NODE_NO}
    --ORDER BY PROC_TM
    ) A
WHERE ROWNUM <= ${ROW_CNT}


