SELECT /*+ 송재복::/gcb/cmnDatSynch/gcbCmnDatAplApcHisEbi/TBGCBDM11/retvLstGcbCmnDatAplApcHisCtt */ 
       A.공통데이터적용신청년월일
     , A.어플리케이션코드
     , A.공통데이터적용코드
     , A.공통데이터적용세부식별내용
     , A.적용시작년월일
     , A.공통데이터적용일련번호
     , A.데이터변경작업구분코드
     , A.공통데이터적용레코드내용
  FROM INSTC.TBGCBDM11 A 
 WHERE A.공통데이터테이블명     = ${공통데이터테이블명}
   AND A.공통데이터적용신청년월일  = ${공통데이터적용신청년월일}
   AND A.어플리케이션코드   BETWEEN ${어플리케이션코드1} AND ${어플리케이션코드2} 
   AND A.공통데이터적용코드  BETWEEN ${공통데이터적용코드1} AND ${공통데이터적용코드2}
   AND A.공통데이터적용세부식별내용 >= ${공통데이터적용세부식별내용}
   AND (A.적용시작년월일       > ${적용시작년월일}
    OR (A.적용시작년월일       = ${적용시작년월일}
   AND A.공통데이터적용일련번호   >= ${공통데이터적용일련번호}))
   AND ( ${공통데이터적용코드출력내용} IS NULL OR A.공통데이터적용코드출력내용  LIKE ${공통데이터적용코드출력내용} || '%' )


