SELECT /*+ 송재복::/gcb/cmnDatSynch/gcbCmnDatAplMgCdNbrEbi/TBGCBDM10/retvGcbCmnDatAplMgCdNbrByMdMgCd */ 
       A.변경관리코드
  FROM INSTC.TBGCBDM10 A        /* GCB공통데이터적용관리코드채번 */
 WHERE A.공통데이터테이블명  = ${공통데이터테이블명}
   AND A.공통데이터키식별내용 = ${공통데이터키식별내용}
   AND A.공통데이터키일련번호 = ${공통데이터키일련번호}


SELECT /*+ 송재복::/gcb/cmnDatSynch/gcbCmnDatAplMgCdNbrEbi/TBGCBDM10/retvGcbCmnDatAplMgCdNbrByLstMdMgCd */ 
       MAX(A.변경관리코드) 변경관리코드
  FROM INSTC.TBGCBDM10 A        /* GCB공통데이터적용관리코드채번 */
 WHERE SUBSTR(A.변경관리코드,1,1) = ${변경관리코드}
   AND A.변경관리코드 BETWEEN ${변경관리코드1} AND ${변경관리코드2}


INSERT /*+ 송재복::/gcb/cmnDatSynch/gcbCmnDatAplMgCdNbrEbi/TBGCBDM10/regtGcbCmnDatAplMgCdNbr */
  INTO INSTC.TBGCBDM10        /* GCB공통데이터적용관리코드채번 */
(
   변경관리코드
 , 공통데이터테이블명
 , 공통데이터키식별내용
 , 공통데이터키일련번호
 , 공통데이터적용코드출력내용
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${변경관리코드}
 ,${공통데이터테이블명}
 ,${공통데이터키식별내용}
 ,${공통데이터키일련번호}
 ,${공통데이터적용코드출력내용}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)
