statements.statement:

SELECT /*+ 송재복::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvLstGCB_ItEmpDtyDtlEmpUntBwk */ 
       A.직원번호
     , A.담당어플리케이션코드
     , A.단위업무구분코드
     , A.한글직원명 
  FROM INSTC.TBGCBHR03 A 
 WHERE A.직원번호    = ${직원번호}
   AND A.적용시작년월일 <= ${적용시작년월일}
   AND A.적용종료년월일 >= ${적용종료년월일}


SELECT /*+ 송재복::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvLstGCB_ItEmpDtyDtlBlngTemEmpUntBwk */ 
       A.직원번호
     , A.담당어플리케이션코드
     , A.단위업무구분코드
     , A.한글직원명 
  FROM INSTC.TBGCBHR03 A 
 WHERE A.소속IT팀구분코드 = ${소속IT팀구분코드} 
   AND A.적용종료년월일   >= ${적용종료년월일}
   AND A.적용시작년월일   <= ${적용시작년월일} 
ORDER BY A.직원번호


SELECT /*+ 송재복::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvLstGCB_ItEmpDtyDtlEmpRsbUppl */ 
       DISTINCT(A.담당어플리케이션코드) 담당어플리케이션코드 
  FROM INSTC.TBGCBHR03 A 
 WHERE A.직원번호       = ${직원번호}
   AND A.담당어플리케이션코드 LIKE ${담당어플리케이션코드}
   AND ${기준년월일} BETWEEN A.적용시작년월일 AND A.적용종료년월일


SELECT /*+ 김동현::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvLstGcbLdgAmdItRsbp */ 
       A.직원번호
     , LISTAGG(A.담당어플리케이션코드, NVL(${데이터구분_V3}, '#|@')) WITHIN GROUP(ORDER BY A.직원번호) 담당어플리케이션코드_V1000
  FROM INSTC.TBGCBHR03 A
 WHERE A.소속IT팀구분코드 = ${소속IT팀구분코드}
   AND A.적용시작년월일 <= ${적용시작년월일}
   AND A.적용종료년월일 >= ${적용종료년월일}
   AND A.담당어플리케이션코드 <> '999'
GROUP BY A.직원번호


SELECT /*+ 김동현::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvGCB_ItEmpDtyDtlByBlngItTemDtcdUpplNcn */ 
       COUNT(*) 조회건수_N3
  FROM INSTC.TBGCBHR03 A 
 WHERE A.직원번호       = ${직원번호}
   AND A.담당어플리케이션코드 = ${담당어플리케이션코드}
   AND A.적용시작년월일    <= ${적용시작년월일}
   AND A.적용종료년월일    >= ${적용종료년월일}
   AND A.소속IT팀구분코드  = ${소속IT팀구분코드}


SELECT /*+ 김동현::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvLstGcbLdgAmdItEmp */ 
       DISTINCT A.직원번호
  FROM INSTC.TBGCBHR03 A 
 WHERE (A.담당어플리케이션코드 = ${담당어플리케이션코드}
        OR ${담당어플리케이션코드} IS NULL)
   AND (A.단위업무구분코드 = ${단위업무구분코드}
        OR ${단위업무구분코드} IS NULL)
   AND (A.소속IT부서구분코드 = ${소속IT부서구분코드}
        OR ${소속IT부서구분코드} IS NULL)
   AND (A.소속IT팀구분코드 = ${소속IT팀구분코드}
        OR ${소속IT팀구분코드} IS NULL)
   AND (A.회계부점코드 = ${회계부점코드}
        OR ${회계부점코드} IS NULL)        
   AND A.적용시작년월일    <= ${적용시작년월일}
   AND A.적용종료년월일    >= ${적용종료년월일}
   AND A.직원번호 LIKE 'K%'


SELECT /*+ 김동현::/gcb/empInfMg/gCB_ItEmpDtyDtlEbi/TBGCBHR03/retvLstGCB_ItEmpDtyDtlByBlngFmalTemm */ 
       DISTINCT A.직원번호
  FROM INSTC.TBGCBHR03 A        /* GCB_IT직원직무상세 */
 WHERE A.적용시작년월일 <= ${적용시작년월일}
   AND A.적용종료년월일 >= ${적용종료년월일}
   AND A.소속IT팀구분코드 = ${소속IT팀구분코드}
   AND A.직원번호 LIKE 'K%'
