statements.statement:

DELETE /*+ 김남식::/gcb/empInfMg/gcbEmpClwrAltmDtlEbi/TBGCBHM01/delGcbEmpClwrAltmDtl */
  FROM INSTC.TBGCBHM01  
 WHERE 부점코드    = ${부점코드}
   AND 사무분담코드  = ${사무분담코드}
   AND 직원번호    = ${직원번호}
   AND 적용시작년월일 = ${적용시작년월일}


UPDATE /*+ 김남식::/gcb/empInfMg/gcbEmpClwrAltmDtlEbi/TBGCBHM01/uptGcbEmpClwrAltmDtl */ 
       INSTC.TBGCBHM01 
   SET 적용종료년월일    = ${적용종료년월일}
     , 시스템최종갱신일시  = SYSTIMESTAMP
     , 시스템최종갱신식별자 = ${시스템최종갱신식별자}
     , 시스템최종거래일시  = ${시스템최종거래일시}
 WHERE 부점코드    = ${부점코드}
   AND 사무분담코드  = ${사무분담코드}
   AND 직원번호    = ${직원번호}
   AND 적용시작년월일 = ${적용시작년월일}


INSERT /*+ 김남식::/gcb/empInfMg/gcbEmpClwrAltmDtlEbi/TBGCBHM01/regtGcbEmpClwrAltmDtl */
  INTO INSTC.TBGCBHM01
(
   부점코드
 , 사무분담코드
 , 직원번호
 , 적용시작년월일
 , 적용종료년월일
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${부점코드}
 ,${사무분담코드}
 ,${직원번호}
 ,${적용시작년월일}
 ,${적용종료년월일}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


INSERT /*+ 김민교::/gcb/empInfMg/gcbEmpClwrAltmDtlEbi/TBGCBHM01/regtGcbEmpClwrAltmDtlInqByClwrAltmInfErtm */
  INTO INSTC.TBGCBHM01        /* GCB직원사무분담상세 */
(
   부점코드
 , 사무분담코드
 , 직원번호
 , 적용시작년월일
 , 적용종료년월일
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
 , 대직여부
)
VALUES
(
  ${부점코드}
 ,${사무분담코드}
 ,${직원번호}
 ,${적용시작년월일}
 ,${적용종료년월일}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
 ,${대직여부}
)


DELETE /*+ 김민교::/gcb/empInfMg/gcbEmpClwrAltmDtlEbi/TBGCBHM01/delGcbEmpClwrAltmDtlInqByClwrAltmInfErtm */
  FROM INSTC.TBGCBHM01        /* GCB직원사무분담상세 */
 WHERE 부점코드    = ${부점코드}
   AND 사무분담코드  = ${사무분담코드}
   AND 직원번호    = ${직원번호}
   AND 적용시작년월일 = ${적용시작년월일}


SELECT /*+ 김민교::/gcb/empInfMg/gcbEmpClwrAltmDtlEbi/TBGCBHM01/retvGcbEmpClwrAltmDtlInqBySpsrAtrVldYn */ 
       COUNT(*) 조회건수_N3
  FROM INSTC.TBGCBHM01 A        /* GCB직원사무분담상세 */
 WHERE A.부점코드    = ${부점코드}
   AND A.사무분담코드  = ${사무분담코드}
   AND A.직원번호    = ${직원번호}
   AND ${적용시작년월일} BETWEEN A.적용시작년월일 AND A.적용종료년월일
