statements.statement:

INSERT /*+ 위상규::/gcb/etcInfMg/gCB_UmsExcelEmlSndDatHisEbi/TBGCBUM02/regtGCB_UmsExcelEmlSndDatHis */
  INTO INSTC.TBGCBUM02        /* GCB_UMS엑셀이메일발송데이터내역 */
(
   이메일발송요청등록일시
 , 고객식별자
 , 고객관리번호
 , 엑셀서식식별번호
 , 엑셀서식일련번호
 , 엑셀서식출력구분코드
 , 엑셀서식출력내용
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${이메일발송요청등록일시}
 ,${고객식별자}
 ,${고객관리번호}
 ,${엑셀서식식별번호}
 ,${엑셀서식일련번호}
 ,${엑셀서식출력구분코드}
 ,${엑셀서식출력내용}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


SELECT /*+ 위상규::/gcb/etcInfMg/gCB_UmsExcelEmlSndDatHisEbi/TBGCBUM02/retvGCB_UmsExcelEmlSndDatHisNcn */ 
       COUNT(1) 건수_N15
  FROM INSTC.TBGCBUM02 A        /* GCB_UMS엑셀이메일발송데이터내역 */
 WHERE A.이메일발송요청등록일시 = ${이메일발송요청등록일시}
   AND A.고객식별자       = ${고객식별자}
   AND A.고객관리번호      = ${고객관리번호}
   AND A.엑셀서식식별번호    = ${엑셀서식식별번호}
