INSERT /*+ 박재훈::/gcb/ingCdMg/gcbClwrAltmCdEbi/TBGCBHM02/regtGcbClwrAltmCd */
  INTO INSTC.TBGCBHM02
(
   사무분담코드
 , 사무분담코드명
 , 적용시작년월일
 , 적용종료년월일
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${사무분담코드}
 ,${사무분담코드명}
 ,${적용시작년월일}
 ,${적용종료년월일}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


UPDATE /*+ 박재훈::/gcb/ingCdMg/gcbClwrAltmCdEbi/TBGCBHM02/uptGcbClwrAltmCd */ 
       INSTC.TBGCBHM02 
   SET 사무분담코드명    = ${사무분담코드명}
