statements.statement:

INSERT /*+ 노광희::/gcb/itObstMgSpt/gcbBchTtmVlnRulInfEbi/TBGCBEM02/regtGcbBchTtmVlnRulInf */
  INTO INSTC.TBGCBEM02        /* GCB배치시간검증규칙정보 */
(
   JAS작업ID
 , 배치시간검증기준구분코드
 , 배치작업예상소요시간
 , 배치작업기준일구분코드
 , 배치작업종료목표시분
 , IT운영SMS전송대상그룹식별내용
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${JAS작업ID}
 ,${배치시간검증기준구분코드}
 ,${배치작업예상소요시간}
 ,${배치작업기준일구분코드}
 ,${배치작업종료목표시분}
 ,${IT운영SMS전송대상그룹식별내용}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


UPDATE /*+ 노광희::/gcb/itObstMgSpt/gcbBchTtmVlnRulInfEbi/TBGCBEM02/uptGcbBchTtmVlnRulInf */ 
       INSTC.TBGCBEM02        /* GCB배치시간검증규칙정보 */
   SET 배치작업예상소요시간        = ${배치작업예상소요시간}
     , 배치작업기준일구분코드       = ${배치작업기준일구분코드}
     , 배치작업종료목표시분        = ${배치작업종료목표시분}
     , IT운영SMS전송대상그룹식별내용 = ${IT운영SMS전송대상그룹식별내용}
     , 시스템최종갱신일시         = SYSTIMESTAMP
     , 시스템최종갱신식별자        = ${시스템최종갱신식별자}
     , 시스템최종거래일시         = ${시스템최종거래일시}
 WHERE JAS작업ID      = ${JAS작업ID}
   AND 배치시간검증기준구분코드 = ${배치시간검증기준구분코드}


DELETE /*+ 노광희::/gcb/itObstMgSpt/gcbBchTtmVlnRulInfEbi/TBGCBEM02/delGcbBchTtmVlnRulInf */
  FROM INSTC.TBGCBEM02        /* GCB배치시간검증규칙정보 */
 WHERE JAS작업ID      = ${JAS작업ID}
   AND 배치시간검증기준구분코드 = ${배치시간검증기준구분코드}
