statements.statement:

INSERT /*+ 노광희::/gcb/itObstMgSpt/gcbBchVlnErrPhsEbi/TBGCBEM08/regtGcbBchVlnErrPhs */
  INTO INSTC.TBGCBEM08        /* GCB배치검증오류이력 */
(
   배치작업시작일시
 , JAS작업ID
 , 배치검증번호
 , 배치검증오류구분코드
 , 배치검증기준데이터결과값
 , 배치검증오류데이터결과값
 , JOBID
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${배치작업시작일시}
 ,${JAS작업ID}
 ,${배치검증번호}
 ,${배치검증오류구분코드}
 ,${배치검증기준데이터결과값}
 ,${배치검증오류데이터결과값}
 ,${JOBID}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


UPDATE /*+ 노광희::/gcb/itObstMgSpt/gcbBchVlnErrPhsEbi/TBGCBEM08/uptGcbBchVlnErrPhs */ 
       INSTC.TBGCBEM08        /* GCB배치검증오류이력 */
