statements.statement:

INSERT /*+ 김민교::/gcb/itObstMgSpt/gcbBchVlnRulInfEbi/TBGCBEM01/regtGcbBchVlnRulInf */
  INTO INSTC.TBGCBEM01        /* GCB배치검증규칙정보 */
(
   JAS작업ID
 , 배치검증번호
 , 배치검증규칙내용
 , 참조통계데이터구분코드
 , 배치검증유형구분코드
 , 배치검증방법구분코드
 , 배치검증오차범위값
 , 배치검증임계값
 , 전체건수검증여부
 , 성공건수검증여부
 , 오류건수검증여부
 , 생략건수검증여부
 , 작업오류처리여부
 , 삭제여부
 , IT운영SMS전송대상그룹식별내용
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${JAS작업ID}
 ,(SELECT NVL(MAX(A.배치검증번호), 0) + 1 FROM INSTC.TBGCBEM01 A WHERE A.JAS작업ID = ${JAS작업ID})
 ,${배치검증규칙내용}
 ,${참조통계데이터구분코드}
 ,${배치검증유형구분코드}
 ,${배치검증방법구분코드}
 ,${배치검증오차범위값}
 ,${배치검증임계값}
 ,${전체건수검증여부}
 ,${성공건수검증여부}
 ,${오류건수검증여부}
 ,${생략건수검증여부}
 ,${작업오류처리여부}
 ,${삭제여부}
 ,${IT운영SMS전송대상그룹식별내용}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


DELETE /*+ 김민교::/gcb/itObstMgSpt/gcbBchVlnRulInfEbi/TBGCBEM01/delGcbBchVlnRulInf */
  FROM INSTC.TBGCBEM01        /* GCB배치검증규칙정보 */
 WHERE JAS작업ID = ${JAS작업ID}
   AND 배치검증번호  = ${배치검증번호}


UPDATE /*+ 김민교::/gcb/itObstMgSpt/gcbBchVlnRulInfEbi/TBGCBEM01/uptGcbBchVlnRulInf */ 
       INSTC.TBGCBEM01        /* GCB배치검증규칙정보 */
   SET 배치검증규칙내용          = ${배치검증규칙내용}
     , 참조통계데이터구분코드       = ${참조통계데이터구분코드}
     , 배치검증유형구분코드        = ${배치검증유형구분코드}
     , 배치검증방법구분코드        = ${배치검증방법구분코드}
     , 배치검증오차범위값         = ${배치검증오차범위값}
     , 배치검증임계값           = ${배치검증임계값}
     , 전체건수검증여부          = ${전체건수검증여부}
     , 성공건수검증여부          = ${성공건수검증여부}
     , 오류건수검증여부          = ${오류건수검증여부}
     , 생략건수검증여부          = ${생략건수검증여부}
     , 작업오류처리여부          = ${작업오류처리여부}
     , 삭제여부              = ${삭제여부}
     , IT운영SMS전송대상그룹식별내용 = ${IT운영SMS전송대상그룹식별내용}
     , 시스템최종갱신일시         = SYSTIMESTAMP
     , 시스템최종갱신식별자        = ${시스템최종갱신식별자}
     , 시스템최종거래일시         = ${시스템최종거래일시}
 WHERE JAS작업ID = ${JAS작업ID}
   AND 배치검증번호  = ${배치검증번호}
