statements.statement:

INSERT /*+ 위상규::/gcb/itOpSmsMg/gCB_ItOpSmsTrsCtroBseEbi/TBGCBSM01/regtGCB_ItOpSmsTrsCtroBse */
  INTO INSTC.TBGCBSM01
(
   시스템운영환경구분코드
 , 거래유형구분코드
 , IT운영SMS종류명
 , 사용여부
 , IT운영SMS제어내용
 , SMS전송통제시작일시
 , SMS전송통제종료일시
 , IT운영SMS비고
 , 등록직원번호
 , 등록년월일
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${시스템운영환경구분코드}
 ,${거래유형구분코드}
 ,${IT운영SMS종류명}
 ,${사용여부}
 ,${IT운영SMS제어내용}
 ,${SMS전송통제시작일시}
 ,${SMS전송통제종료일시}
 ,${IT운영SMS비고}
 ,${등록직원번호}
 ,${등록년월일}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


UPDATE /*+ 위상규::/gcb/itOpSmsMg/gCB_ItOpSmsTrsCtroBseEbi/TBGCBSM01/uptGCB_ItOpSmsTrsCtroBse */ 
       INSTC.TBGCBSM01 
   SET 사용여부        = ${사용여부}
     , IT운영SMS제어내용 = ${IT운영SMS제어내용}
     , SMS전송통제시작일시 = ${SMS전송통제시작일시}
     , SMS전송통제종료일시 = ${SMS전송통제종료일시}
     , IT운영SMS비고   = ${IT운영SMS비고}
     , 등록직원번호      = ${등록직원번호}
     , 등록년월일       = ${등록년월일}
     , 시스템최종갱신일시   = SYSTIMESTAMP
     , 시스템최종갱신식별자  = ${시스템최종갱신식별자}
     , 시스템최종거래일시   = ${시스템최종거래일시}
 WHERE 시스템운영환경구분코드 = ${시스템운영환경구분코드}
   AND 거래유형구분코드    = ${거래유형구분코드}
   AND IT운영SMS종류명  = ${IT운영SMS종류명}


DELETE /*+ 위상규::/gcb/itOpSmsMg/gCB_ItOpSmsTrsCtroBseEbi/TBGCBSM01/delGCB_ItOpSmsTrsCtroBse */
  FROM INSTC.TBGCBSM01  
 WHERE 시스템운영환경구분코드 = ${시스템운영환경구분코드}
   AND 거래유형구분코드    = ${거래유형구분코드}
   AND IT운영SMS종류명  = ${IT운영SMS종류명}
