INSERT /*+ 김민교::/gcb/ldgAmdMg/gcbLdgAmdDatHisEbi/TBGCBLA16/regtGcbLdgAmdDatHis */
  INTO INSTC.TBGCBLA16
(
   원장수정의뢰번호
 , 원장수정IT담당팀구분코드
 , 원장수정대상서버구분코드
 , 어플리케이션코드
 , 원장수정담당직원번호
 , 원장수정처리SQL일련번호
 , 원장수정대상컬럼일련번호
 , 원장수정대상테이블명
 , 원장수정대상테이블PK내용
 , 원장수정데이터처리구분코드
 , 원장수정대상컬럼명
 , 원장수정대상컬럼타입내용
 , 원장수정대상컬럼길이
 , 원장수정전데이터내용
 , 원장수정후데이터내용
 , 원장수정첨부파일그룹번호
 , 시스템최종갱신일시
 , 시스템최종갱신식별자
 , 시스템최종거래일시
)
VALUES
(
  ${원장수정의뢰번호}
 ,${원장수정IT담당팀구분코드}
 ,${원장수정대상서버구분코드}
 ,${어플리케이션코드}
 ,${원장수정담당직원번호}
 ,${원장수정처리SQL일련번호}
 ,(DECODE(${원장수정대상컬럼일련번호}, '0', (SELECT NVL(MAX(원장수정대상컬럼일련번호), 0)+1
                                        FROM INSTC.TBGCBLA16
                                       WHERE 원장수정의뢰번호 = ${원장수정의뢰번호}
                                         AND 원장수정IT담당팀구분코드 = ${원장수정IT담당팀구분코드}
                                         AND 원장수정대상서버구분코드 = ${원장수정대상서버구분코드}
                                         AND 어플리케이션코드 = ${어플리케이션코드}
                                         AND 원장수정담당직원번호 = ${원장수정담당직원번호}
                                         AND 원장수정처리SQL일련번호 = ${원장수정처리SQL일련번호}), ${원장수정대상컬럼일련번호}))
 ,${원장수정대상테이블명}
 ,${원장수정대상테이블PK내용}
 ,${원장수정데이터처리구분코드}
 ,${원장수정대상컬럼명}
 ,${원장수정대상컬럼타입내용}
 ,${원장수정대상컬럼길이}
 ,${원장수정전데이터내용}
 ,${원장수정후데이터내용}
 ,${원장수정첨부파일그룹번호}
 ,SYSTIMESTAMP
 ,${시스템최종갱신식별자}
 ,${시스템최종거래일시}
)


SELECT /*+ 김동현::/gcb/ldgAmdMg/gcbLdgAmdDatHisEbi/TBGCBLA16/retvGcbLdgAmdDatHisByRegNcn */ 
       DECODE(COUNT(*), 0, 0, 1) 조회건수_N2
  FROM INSTC.TBGCBLA16 A 
 WHERE A.원장수정의뢰번호      = ${원장수정의뢰번호}
   AND A.원장수정IT담당팀구분코드 = ${원장수정IT담당팀구분코드}
   AND A.원장수정대상서버구분코드  = ${원장수정대상서버구분코드}
   AND A.어플리케이션코드      = ${어플리케이션코드}
   AND A.원장수정담당직원번호    = ${원장수정담당직원번호}


SELECT /*+ 김동현::/gcb/ldgAmdMg/gcbLdgAmdDatHisEbi/TBGCBLA16/retvLstGcbLdgAmdDatExe */ 
       DISTINCT A.원장수정의뢰번호
     , A.원장수정IT담당팀구분코드
     , A.원장수정대상서버구분코드
     , A.어플리케이션코드
     , A.원장수정담당직원번호
     , A.원장수정처리SQL일련번호
  FROM INSTC.TBGCBLA16 A 
 WHERE A.원장수정의뢰번호      = ${원장수정의뢰번호}
   AND A.원장수정IT담당팀구분코드 = ${원장수정IT담당팀구분코드}
   AND A.원장수정대상서버구분코드  = ${원장수정대상서버구분코드}
   AND A.어플리케이션코드      = ${어플리케이션코드}
   AND A.원장수정담당직원번호    = ${원장수정담당직원번호}
ORDER BY A.원장수정처리SQL일련번호 ASC
