SELECT /*+ 김민교::/gcb/ldgAmdMg/gcbLdgAmdRsbpApvHisEbi/TBGCBLA15/retvGcbLdgAmdRsbpApvHisByRsbpApvRst */ 
       COUNT(*) 조회건수_N2
  FROM INSTC.TBGCBLA15 A 
 WHERE A.원장수정의뢰번호      = ${원장수정의뢰번호}
   AND A.원장수정의뢰서진행상태구분코드 IN ('01', '05') /*01(진행중), 05(배정추가)*/
   AND NOT EXISTS (SELECT 1
                 FROM INSTC.TBGCBLA15 B
                WHERE B.원장수정의뢰번호 = ${원장수정의뢰번호}
                  AND B.원장수정IT담당팀구분코드 = ${원장수정IT담당팀구분코드}
                  AND B.원장수정대상서버구분코드 = ${원장수정대상서버구분코드}
                  AND B.어플리케이션코드 = ${어플리케이션코드}
                  AND B.원장수정담당직원번호 = ${원장수정담당직원번호}
                  AND B.원장수정의뢰번호 = A.원장수정의뢰번호
                  AND B.원장수정IT담당팀구분코드 = A.원장수정IT담당팀구분코드
                  AND B.원장수정대상서버구분코드 = A.원장수정대상서버구분코드
                  AND B.어플리케이션코드 = A.어플리케이션코드
                  AND B.원장수정담당직원번호 = A.원장수정담당직원번호)


